import Api from "../utils/api";
import loadCompaniesSuccess from "./companyActions";

export function loadCompanies() {
  return function(dispatch) {
    return Api.getCompanies()
      .then(companies => {
        dispatch("dddd");
      })
      .catch(error => {
        throw error;
      });
  };
}
