import {
  GET_COMPANIES,
  DELETE_COMPANY,
  UPDATE_COMPANY,
  CREATE_COMPANY,
  GET_COMPANY,
  FAILED
} from "../types/companies";

export const get = data => {
  return {
    type: GET_COMPANIES,
    payload: data
  };
};

export const getComp = data => {
  return {
    type: GET_COMPANY,
    payload: data
  };
};

export const create = data => {
  return {
    type: CREATE_COMPANY,
    payload: data
  };
};

export const edit = data => {
  return {
    type: UPDATE_COMPANY,
    payload: data
  };
};

export const err = error => {
  return {
    type: FAILED,
    error
  };
};
