import React, { Component } from 'react';
import axios from 'axios';
import {  SERVER_URL2 } from "../utils/JWTAuth.js";


class UpdateEmployee  extends Component {
    constructor(props) {
        super(props);

        this.setState({
            first_name: '',
            last_name: '',
            company_id: '',
            email: '',
            phone: '',
          })
    }

  onChangeEmployeeFirstname = (e) => {
        this.setState({
            first_name: e.target.value
        });
    }

    onChangeEmployeeLastname = (e) => {
        this.setState({
            last_name: e.target.value
        });
    }

    onChangeEmployeeEmail = (e) => {
        this.setState({
            email: e.target.value
        });
    }

    onChangeEmployeePhone = (e) => {
        this.setState({
            phone: e.target.value
        });
    }

    onChangeEmployeeCompany = (e) => {
        this.setState({
            company_id: e.target.value
        });
    }

    onSubmit = (e) => {

        console.log(window.localStorage.getItem('access_token'));
        e.preventDefault();
        const {firstname, lastname, company, email, phone}  = this.state;
        console.log(`Form submitted:`);


       const newEmployee = {
            firstname,
            lastname,
            company,
            email,
            phone
        };


     /*     axios.put( SERVER_URL2+'/api/employee', newEmployee)
         .then(res => console.log(res.data));*/


          this.setState({
                firstname: '',
                lastname: '',
                company: '',
                email: '',
                phone: ''
          })
    }

    render() {
        const {firstname, lastname, company, email, phone} = this.state;
        return (
            <div style={{marginTop: 10}}>
                <h3>Create New Employee</h3>
                <form encType="multipart/form-data" onSubmit={this.onSubmit}>
                    <div className="form-group"> 
                        <label>Firstname: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={firstname}
                                onChange={this.onChangeEmployeeFirstname}
                                />
                    </div>
                    <div className="form-group">
                        <label>Lastname:</label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={lastname}
                                onChange={this.onChangeEmployeeLastname}
                                />
                    </div>

                    <div className="form-group">
                        <label>Company name</label>
                        <select class="form-control" name="company_id">
                                 <option value="company1">company1</option>
                                 <option value="company2">company2</option>
                        </select>
                    </div>


                    <div className="form-group">
                        <label>Email</label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={email}
                                onChange={this.onChangeEmployeeEmail}
                                />
                    </div>



                    <div className="form-group">
                        <label>Phone: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={phone}
                                onChange={this.onChangeEmployeePhone}
                                />
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Create Employee" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}

export default UpdateEmployee;
