import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import {  SERVER_URL2 } from "../utils/JWTAuth.js";

const Companies = props => (
    <tr>
        <td>{props.company.name}</td>
        <td>{props.company.email}</td>
        <td>{props.company.logo}</td>
        <td>{props.company.website}</td>
        <td>
            <Link to={"/companies/"+props.company.id}>Edit</Link>
            <div >Delete</div>
        </td>
    </tr>
)

 class CompaniesList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            companies: [],
            success: ''
        };

    }

    componentDidMount() {
        axios.get(SERVER_URL2+'/api/companies')
            .then(response => {
                this.setState({ companies: response.data });
            })
            .catch(function (error){
                console.log(error);
            })
    }

    removeCompany = (id) => {
        console.log(id);
        const removeFav = this.state.companies.filter(x=> x != id ) 
        console.log(removeFav);
        this.setState({
            companies: removeFav
        })
      }

    deleteCompany = (id) =>  {
        console.log(id);
        this.removeCompany(id);
            axios.delete(SERVER_URL2+'/api/companies/'+id)
            .then((res) => console.log(res))}

  /*  deleteCompanies = (id) => {
        console.log('delete');
    }*/

    companyList = () => {
        // this.deleteCompany()
        return this.state.companies.map((currentcompany, i)=> {
            return  <tr key={i} >
            <td>{currentcompany.name}</td>
            <td>{currentcompany.email}</td>
            <td>{currentcompany.logo}</td>
            <td>{currentcompany.website}</td>
            <td>
                <Link to={"/companies/"+currentcompany.id}>Edit</Link>
                <button onClick={()=>this.deleteCompany(currentcompany.id)}>Delete</button>
            </td>
        </tr>
        })
    }

    render() {
         const  {success} = this.state;
        return (
            <div>
                <h3>Company List</h3>
                <div className="valid-feedback" style={{display: 'block'}}>
                    {success}
                </div>
                <table className="table table-striped" style={{ marginTop: 20 }} >
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Logo</th>
                            <th>Website</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.companyList() }
                    </tbody>
                </table>
            </div>
        )
    }
}

export default CompaniesList;
