import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Companies = props => (
    <tr>
        <td>{props.name}</td>
        <td>{props.email}</td>
        <td>{props.logo}</td>
        <td>{props.website}</td>
        <td>
            <Link to={"/edit/"+props.id}>Edit</Link>
        </td>
    </tr>
)

 class CompaniesList extends Component {

    constructor(props) {
        super(props);
        this.state = {companies: []};
    }

    componentDidMount() {
        axios.get('http://127.0.0.1:8000/api/companies')
            .then(response => {
                this.setState({ companies: response.data });
            })
            .catch(function (error){
                console.log(error);
            })
    }

    companyList() {
        return this.state.companies.map(function(currentcompany, i){
            return <Companies company={currentcompany} key={i} />;
        })
    }

    render() {
        return (
            <div>
                <h3>Company List</h3>
                <table className="table table-striped" style={{ marginTop: 20 }} >
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Logo</th>
                            <th>Website</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.companyList() }
                    </tbody>
                </table>
            </div>
        )
    }
}

export default CompaniesList;
