export const getCompanies = data => {
  return {
    type: GET_COMPANIES,
    data
  };
};

export const getAllCompanies = () => {
  return dispatch => {
    return axios
      .get(apiUrl)
      .then(response => {
        dispatch(getCompanies(response.data));
      })
      .catch(error => {
        throw error;
      });
  };
};
