import React from 'react';
import { BrowserRouter as Router, Route, Link, Switch } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import CreateCompanies from "./pages/create-companies.component";
import CompaniesList from "./pages/companies-list.component";
import Login from "./pages/login";
import { login, register, logout } from "./utils/JWTAuth.js";
//import EditTodo from "./components/edit-todo.component";
//import TodosList from "./components/todos-list.component";
import logo from './logo.svg';
import './App.css';



class App extends Component {


  async login(){
    let info = {
      email: "kaima.abbes@email.com",
      password: "123456789"
    };

    await login(info);

  }



  render() {
  return (
    <div className="container">
    <div className="row">
      <h1>React JWT Authentication Example</h1>

      <button className="btn btn-primary" onClick = { this.register }>Sign up</button>

      <button className="btn btn-primary" onClick = { this.login }>Log in</button>

      <button className="btn btn-primary" onClick = { logout }>Log out</button>

    </div>
    </div>

  );
}}


export default  App;
