import React, { Component } from 'react';

class CreateCompanies extends Component {
    constructor(props) {
        super(props);

        this.state = {
            name: '',
            email: '',
            logo: '',
            website: ''
        }
    }

    onChangeCompaniesName = (e) => {
        this.setState({
            name: e.target.value
        });
    }

    onChangeCompaniesEmail = (e) => {
        this.setState({
            email: e.target.value
        });
    }

    onChangeCompaniesLogo = (e) => {
        this.setState({
            logo: e.target.value
        });
    }

    onChangeCompaniesWebsite = (e) => {
        this.setState({
            website: e.target.value
        });
    }

    onSubmit = (e) => {
        e.preventDefault();

        console.log(`Form submitted:`);
        console.log(`Name: ${this.state.name}`);
        console.log(`Email: ${this.state.email}`);
        console.log(`Logo: ${this.state.logo}`);
        console.log(`Website: ${this.state.website}`);

        this.setState({
            name: '',
            email: '',
            logo: '',
            website: ''
        })
    }

    render() {
        return (
            <div style={{marginTop: 10}}>
                <h3>Create New Company</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group"> 
                        <label>Name: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={this.state.name}
                                onChange={this.onChangeCompaniesName}
                                />
                    </div>
                    <div className="form-group">
                        <label>Email: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={this.state.email}
                                onChange={this.onChangeCompaniesEmail}
                                />
                    </div>

                    <div className="form-group">
                        <label>Logo: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={this.state.logo}
                                onChange={this.onChangeCompaniesLogo}
                                />
                    </div>

                    <div className="form-group">
                        <label>Website: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={this.state.website}
                                onChange={this.onChangeCompaniesWebsite}
                                />
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Create Todo" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}

export default CreateCompanies;
