import React, { Component } from "react";
import Api from "../../utils/api";
import Input from "../../components/basic/input-button";
import Submit from "../../components/basic/submit-button";
import Select from "../../components/basic/select";
import Errors from "../../components/basic/errors";
import Success from "../../components/basic/success";
import { connect } from "react-redux";
import {
  mapStateToProps,
  mapDispatchToProps
} from "../../store/containers/employees/create";

class CreateEmployee extends Component {
  constructor(props) {
    super(props);

    this.state = {
      first_name: "",
      last_name: "",
      company_id: "",
      email: "",
      phone: "",
      companies: [],
      nameerror: "",
      lastnameerror: "",
      success: ""
    };
  }

  async componentDidMount() {
    try {
      let res = await Api.getCompanies();
      if (res.status === 200) {
        this.setState({ companies: res.data });
      }
    } catch (error) {}
  }

  onChangeEmployeeFirstname = e => {
    this.setState({
      first_name: e.target.value,
      success: "",
      nameerror: ""
    });
  };

  onChangeEmployeeLastname = e => {
    this.setState({
      last_name: e.target.value,
      success: "",
      lastnameerror: ""
    });
  };

  onChangeEmployeeEmail = e => {
    this.setState({
      email: e.target.value,
      success: ""
    });
  };

  onChangeEmployeePhone = e => {
    this.setState({
      phone: e.target.value,
      success: ""
    });
  };

  onChangeEmployeeCompany = e => {
    this.setState({
      company_id: e.target.value,
      success: ""
    });
  };

  onSubmit = async e => {
    e.preventDefault();
    const { first_name, last_name, company_id, email, phone } = this.state;
    const newEmployee = {
      first_name,
      last_name,
      email,
      company_id,
      phone
    };

    /* try {
      let response = await Api.createEmployees(newEmployee);
      if (response.status === 201) {
        this.setState({
          success: "Employee added",
          first_name: "",
          last_name: "",
          company_id: "",
          email: "",
          phone: ""
        });
      }
    } catch (error) {
      this.setState({
        nameerror: error.response.data.errors.first_name,
        lastnameerror: error.response.data.errors.last_name
      });
    }*/
    await this.props.CreateEmployees(newEmployee);
    console.log(this.props);
    if (this.props.success === 201) {
      // this.props.history.push("/employee");
      console.log("dddd");
    } else {
      this.setState({
        nameerror: this.props.errors
      });
    }
  };

  companiesList = () => {
    const { companies } = this.state;
    return companies.map(company => {
      return (
        <option value={company.id} key={company.id}>
          {company.name}
        </option>
      );
    });
  };

  render() {
    const {
      first_name,
      last_name,
      email,
      phone,
      nameerror,
      lastnameerror,
      success
    } = this.state;
    return (
      <div style={{ marginTop: 10 }}>
        <h3>Create New Employee</h3>
        <form encType="multipart/form-data" onSubmit={this.onSubmit}>
          <Errors name={nameerror} />
          <Errors name={lastnameerror} />
          <Input
            name="Firstname:"
            type="text"
            value={first_name}
            changeFunction={this.onChangeEmployeeFirstname}
          />
          <Input
            name="Lastname:"
            type="text"
            value={last_name}
            changeFunction={this.onChangeEmployeeLastname}
          />
          <Select
            name="Company name"
            id="company_id"
            changeFunction={this.onChangeEmployeeCompany}
            default="Choose Company"
            optionList={this.companiesList()}
          />
          <Input
            name="Email:"
            type="email"
            value={email}
            changeFunction={this.onChangeEmployeeEmail}
          />
          <Input
            name="Phone:"
            type="text"
            value={phone}
            changeFunction={this.onChangeEmployeePhone}
          />
          <Submit value="Create Employee" />
          <Success name={success} />
        </form>
      </div>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(CreateEmployee);
