import React from "react";
import { connect } from "react-redux";
import { getAllCompanies } from "../actions/companies";

const companyListt = () => {
  if (!this.props.companies.length) {
    return <div>No Company</div>;
  }
  return <div>{console.log(this.props.companies)}</div>;
};

const mapStateToProps = state => {
  return {
    companies: state.companies.data
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getCompanies: dispatch(getAllCompanies())
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(companyListt);
