import React, { Component } from 'react';
import axios from 'axios';
import {  SERVER_URL2 } from "../utils/JWTAuth.js";


class UpdateEmployee  extends Component {
    constructor(props) {
        super(props);

        this.setState({
            first_name: 'rr',
            last_name: 'rrr',
            company_id: '56',
            email: 'tt',
            phone: 'tt',
            errors: '',
            nameerror: '',
            lastnameerror: '',
            success: '',
          })
    }

    componentDidMount() {
        console.log(this.props.match.params.id);
        axios.get(SERVER_URL2+'/api/employees/'+this.props.match.params.id)
            .then(response => console.log(response.data) /*{
                this.setState({
                    first_name:  response.data.first_name == null ? '':  response.data.first_name ,
                    last_name: response.data.last_name== null ? '':  response.data.last_name ,
                    company_id: response.data.company_id== null ? '':  response.data.company_id ,
                    email:  response.data.email== null ? '':  response.data.email ,
                    phone:  response.data.phone==null ? '': response.data.phone
                })   
            },*/
)
            .catch(function (error) {
                console.log(error);
            })
    }

  onChangeEmployeeFirstname = (e) => {
        this.setState({
            first_name: e.target.value
        });
    }

    onChangeEmployeeLastname = (e) => {
        this.setState({
            last_name: e.target.value
        });
    }

    onChangeEmployeeEmail = (e) => {
        this.setState({
            email: e.target.value
        });
    }

    onChangeEmployeePhone = (e) => {
        this.setState({
            phone: e.target.value
        });
    }

    onChangeEmployeeCompany = (e) => {
        this.setState({
            company_id: e.target.value
        });
    }

    onSubmit = (e) => {

        console.log(window.localStorage.getItem('access_token'));
        e.preventDefault();
        const {first_name, last_name, company_id, email, phone}  = this.state;
        console.log(`Form submitted:`);

     /*   const updateEmployee = {
            first_name,
            last_name,
            email,
            company_id,
            phone
        };
*/


     /*     axios.put( SERVER_URL2+'/api/employee', updateEmployee)
         .then(res => console.log(res.data));*/


          this.setState({
            first_name: '',
            last_name: '',
            company_id: '',
            email: '',
            phone: '',
          })
    }

    companiesList = () => {
        const { companies } = this.state;
        return companies.map((company) => {
            return <option value={company.id} key={company.id}>{company.name}</option>
        })
    }

    render() {
        const {first_name, last_name, email, phone} = this.state;
        return (
            <div style={{marginTop: 10}}>
                <h3>Update New Employee</h3>
                <form encType="multipart/form-data" onSubmit={this.onSubmit}>
                    <div className="form-group"> 
                        <label>Firstname: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={first_name}
                                onChange={this.onChangeEmployeeFirstname}
                                />
                    </div>
                    <div className="form-group">
                        <label>Lastname:</label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={last_name}
                                onChange={this.onChangeEmployeeLastname}
                                />
                    </div>

                    <div className="form-group">
                        <label>Company name</label>
                        <select class="form-control" name="company_id" onChange={this.onChangeEmployeeCompany}>
                        <option value=''>Choose Company</option>
                                  {this.companiesList()}
                        </select>
                    </div>


                    <div className="form-group">
                        <label>Email</label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={email}
                                onChange={this.onChangeEmployeeEmail}
                                />
                    </div>



                    <div className="form-group">
                        <label>Phone: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={phone}
                                onChange={this.onChangeEmployeePhone}
                                />
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Create Employee" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}

export default UpdateEmployee;
