import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import {  SERVER_URL2 } from "../utils/JWTAuth.js";

const Companies = props => (
    <tr>
        <td>{props.company.name}</td>
        <td>{props.company.email}</td>
        <td>{props.company.logo}</td>
        <td>{props.company.website}</td>
        <td>
            <Link to={"/companies/"+props.company.id}>Edit</Link>
            <div>Delete</div>
        </td>
    </tr>
)

 class CompaniesList extends Component {

    constructor(props) {
        super(props);
        this.state = {companies: []};
    }

    componentDidMount() {
        axios.get(SERVER_URL2+'/api/companies')
            .then(response => {
                this.setState({ companies: response.data });
            })
            .catch(function (error){
                console.log(error);
            })
    }

    deleteCompany = (e) =>  {
            const id = e.target.value;
            axios.delete(SERVER_URL2+'/api/companies', id)
            .then(() => {
                this.props.getAllCompanies();
      })
    }

  /*  deleteCompanies = (id) => {
        console.log('delete');
    }*/

    companyList() {
        return this.state.companies.map(function(currentcompany, i){
            return <Companies company={currentcompany} key={i} />;
        })
    }

    render() {
        return (
            <div>
                <h3>Company List</h3>
                <table className="table table-striped" style={{ marginTop: 20 }} >
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Logo</th>
                            <th>Website</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.companyList() }
                    </tbody>
                </table>
            </div>
        )
    }
}

export default CompaniesList;
