import React, { Component } from 'react';
import axios from 'axios';

class CreateCompanies extends Component {
    constructor(props) {
        super(props);

        this.state = {
            name: '',
            email: '',
            logo: '',
            website: ''
        }
    }

    onChangeCompaniesName = (e) => {
        this.setState({
            name: e.target.value
        });
    }

    onChangeCompaniesEmail = (e) => {
        this.setState({
            email: e.target.value
        });
    }

    onChangeCompaniesLogo = (e) => {
        this.setState({
            logo: e.target.value
        });
    }

    onChangeCompaniesWebsite = (e) => {
        this.setState({
            website: e.target.value
        });
    }

    onSubmit = (e) => {
        e.preventDefault();
        const { name, email,logo, website }  = this.state;
        console.log(`Form submitted:`);
        console.log(`Name: ${this.state.name}`);
        console.log(`Email: ${this.state.email}`);
        console.log(`Logo: ${this.state.logo}`);
        console.log(`Website: ${this.state.website}`);

        const newCompanies = {
            name,
            email,
            logo,
            website,
        };

        axios.get('http://127.0.0.1:8000/api/companies')
            .then(res => console.log(res.data));

           // let formData = new FormData();    //formdata object




        axios.post('http://127.0.0.1:8000/api/companies', newCompanies)
            .then(res => console.log(res.data));


        this.setState({
            name: '',
            email: '',
            logo: '',
            website: ''
        })
    }

    render() {
        const {email, name, logo, website} = this.state;
        return (
            <div style={{marginTop: 10}}>
                <h3>Create New Company</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group"> 
                        <label>Name: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={name}
                                onChange={this.onChangeCompaniesName}
                                />
                    </div>
                    <div className="form-group">
                        <label>Email: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={email}
                                onChange={this.onChangeCompaniesEmail}
                                />
                    </div>

                    <div className="form-group">
                        <label>Logo: </label>
                        <input 
                                type="file" 
                                className="form-control"
                                value={logo}
                                onChange={this.onChangeCompaniesLogo}
                                />
                    </div>



                    <div className="form-group">
                        <label>Website: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={website}
                                onChange={this.onChangeCompaniesWebsite}
                                />
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Create Company" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}

export default CreateCompanies;
