import Api from "../utils/api";
import * as types from "./actionTypes";

export default function loadCompanies() {
  return function(dispatch) {
    return Api.getCompanies().then(({ companies }) => {
      dispatch(loadCompaniesSuccess(companies));
    });
  };
}
