import React, { Component } from 'react';
import axios from 'axios';
import {  SERVER_URL2 } from "../utils/JWTAuth.js";

class EditCompanies extends Component {
    constructor(props) {
        super(props);

        this.state = {
            name: '',
            email: '',
            logo: '',
            website: '',
        }
    }

    componentDidMount() {
        console.log(this.props.match.params.id);
       //const { handle } = this.props.match.params.id
        axios.get(SERVER_URL2+'api/companies/'+this.props.match.params.id)
            .then(response => {
                this.setState({
                    name:  response.data.name,
                    email: response.data.email,
                    logo: '',
                    website:  response.data.website,
                })   
            },
)
            .catch(function (error) {
                console.log(error);
            })
    }

    onChangeCompaniesName = (e) => {
        this.setState({
            name: e.target.value
        });
    }

    onChangeCompaniesEmail = (e) => {
        this.setState({
            email: e.target.value
        });
    }

    onChangeCompaniesLogo = (e) => {
        this.setState({
            logo: e.target.files[0]
        });
    }

    onChangeCompaniesWebsite = (e) => {
        this.setState({
            website: e.target.value
        });
    }

    onSubmit = (e) =>  {
        e.preventDefault();
        const { name, email,logo, website }  = this.state;
        const updateCompanies = new FormData();
        updateCompanies.append('name', name);
        updateCompanies.append('email', email);
        updateCompanies.append('logo', logo);
        updateCompanies.append('website', website);


        axios.put(SERVER_URL2+'api/update'+this.props.match.params.id, updateCompanies)
            .then(res => console.log(res.data));

       // this.props.history.push('/home');
    }


    render() {
        const {email, name, logo, website} = this.state;
        return (
            <div>
            <h3 align="center">Update Company</h3>
            <form onSubmit={this.onSubmit}>
                <div className="form-group"> 
                    <label>Name: </label>
                    <input  type="text"
                            className="form-control"
                            value={name}
                            onChange={this.onChangeCompaniesName}
                            />
                </div>
                <div className="form-group">
                    <label>Email: </label>
                    <input 
                            type="text" 
                            className="form-control"
                            value={email}
                            onChange={this.onChangeCompaniesEmail}
                            />
                </div>


                <div className="form-group">
                        <label>Logo: </label>
                        <input 
                                type="file" 
                                className="form-control"
                                onChange={this.onChangeCompaniesLogo}
                                />
                    </div>
                    <div className="form-group">
                        <label>Website: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={website}
                                onChange={this.onChangeCompaniesWebsite}
                                />
                    </div>

                <div className="form-group">
                    <input type="submit" value="Update Company" className="btn btn-primary" />
                </div>
            </form>
        </div>
        )
    }

}

export default EditCompanies;
