import React, { Component } from 'react';
import axios from 'axios';
import {  SERVER_URL2 } from "../utils/JWTAuth.js";


class CreateEmployee  extends Component {
    constructor(props) {
        super(props);

        this.state = {
            firstname: '',
            lastname: '',
            company: '',
            email: '',
            phone: '',
            companies: []
        }
    }

    componentDidMount() {
        axios.get(SERVER_URL2+'/api/companies')
            .then(response => {
                this.setState({ companies: response.data });
            })
            .catch(function (error){
                console.log(error);
            })
    }

  onChangeEmployeeFirstname = (e) => {
        this.setState({
            firstname: e.target.value
        });
    }

    onChangeEmployeeLastname = (e) => {
        this.setState({
            lastname: e.target.value
        });
    }

    onChangeEmployeeEmail = (e) => {
        this.setState({
            email: e.target.value
        });
    }

    onChangeEmployeePhone = (e) => {
        this.setState({
            phone: e.target.value
        });
    }

    onChangeEmployeeCompany = (e) => {
        this.setState({
            company: e.target.value
        });
    }

    onSubmit = (e) => {

     //   console.log(window.localStorage.getItem('access_token'));
        e.preventDefault();
        const {firstname, lastname, company, email, phone}  = this.state;
        console.log(`Form submitted:`);


       const newEmployee = {
            firstname,
            lastname,
            company,
            email,
            phone
        };

        console.log(newEmployee);


        /* axios.post( SERVER_URL2+'/api/employee', newEmployee)
         .then(res => console.log(res.data));*/


          this.setState({
                firstname: '',
                lastname: '',
                company: '',
                email: '',
                phone: ''
          })
    }


    companiesList = () => {
        const { companies } = this.state;
        return companies.map((company) => {
            return <option value={company.id} key={company.id}>{company.name}</option>
        })
    }

    render() {
        const {firstname, lastname, company, email, phone} = this.state;
        return (
            <div style={{marginTop: 10}}>
                <h3>Create New Employee</h3>
                <form encType="multipart/form-data" onSubmit={this.onSubmit}>
                    <div className="form-group"> 
                        <label>Firstname: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={firstname}
                                onChange={this.onChangeEmployeeFirstname}
                                />
                    </div>
                    <div className="form-group">
                        <label>Lastname:</label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={lastname}
                                onChange={this.onChangeEmployeeLastname}
                                />
                    </div>

                    <div className="form-group">
                        <label>Company name</label>
                        <select className="form-control" name="company_id"  onChange={this.onChangeEmployeeCompany}>
                                 <option value="company1">company1</option>
                                 <option value="company2">company2</option>
                        </select>
                    </div>


                    <div className="form-group">
                        <label>Email</label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={email}
                                onChange={this.onChangeEmployeeEmail}
                                />
                    </div>  

                    <div className="form-group">
                        <label>Phone: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={phone}
                                onChange={this.onChangeEmployeePhone}
                                />
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Create Employee" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}

export default CreateEmployee;
