import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import {  SERVER_URL2 } from "../utils/JWTAuth.js";

const Employees = props => (
    <tr>
        <td>{props.employee.first_name}</td>
        <td>{props.employee.last_name}</td>
        <td>{props.employee.company_id}</td>
        <td>{props.employee.email}</td>
        <td>{props.employee.phone}</td>
        <td>
            <Link to={"/employees/"+props.employee.id}>Edit</Link> 
            <button onClick={()=>this.deleteCompany(props.employee.id)}>Delete</button>
        </td>
    </tr>
)

 class EmployeesList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            employees: [],
            error: ''
        };
    }

    componentDidMount() {
        axios.get(SERVER_URL2+'/api/employees')
            .then(response =>   {
              this.setState({ employees: response.data });
        })
        .catch(err => {
            this.setState({
                error: err.response.data.error,
            })
        })
    }


    removeEmployee = (id) => {
        console.log(id);
        const removeFav = this.state.employees.filter(employee => employee.id != id ) 
        console.log(removeFav);
        this.setState({
            compaemployeesnies: removeFav
        })
      }


    deleteEmployee = (id) =>  {
        this.removeEmployee(id);
            axios.delete(SERVER_URL2+'/api/employees/'+id)
            .then((res) =>  this.setState({
                success: 'Employee deleted succesfully',
            }))
    }

    employeesList() {
        return this.state.employees.map((currentemployee, i)=> {
            return <Employees employee={currentemployee} key={i} />;
        })
    }

    render() {
        return (
            <div>
                <h3>Employees List</h3>
                <table className="table table-striped" style={{ marginTop: 20 }} >
                    <thead>
                        <tr>
                            <th>firstname</th>
                            <th>lastname</th>
                            <th>company</th>
                            <th>email</th>
                            <th>phone</th>
                            <th>actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.employeesList() }
                    </tbody>
                </table>
            </div>
        )
    }
}

export default EmployeesList;
